rules.click:
// Resume application
Form.resumeForm("resumeDialog", item.properties.trackingCodeField, item.properties.challengeAnswerField).catch(function() {
alert("Could not resume application with the details provided");
});

rules.eq:
if(data.brandJson && data.brandJson !== "") {
  // Use dynamic branding supplied via form pre-fill
  return JSON.parse(data.brandJson);
} else {
  // Use default branding
  return data.brandDefaultJson;
}

/* 
 * Return one of the following options:
 * - "navigator"
 * - "progress-bar"
 * 
 * "navigator" is the default option if no valid option is specified
 */

if(data.brand !== false) {
  return data.brand.progressIndicator;
}

rules.click:
// Navigate backwards 1 page
if (typeof Narrator === "undefined" || Narrator.pageMap.length == 0) {
  Form.$Pages.move(false);
} else {
  Narrator.navigate("back");
}

// Navigate forwards 1 page
if (typeof Narrator === "undefined" || Narrator.pageMap.length == 0) {
  Form.$Pages.move(true);
} else {
  Narrator.navigate("forward");
}

// Submit the form
Form.submit(item.properties.submissionMethod, item.properties.legacyMethod).then(function(response){
    Form.fireRule("onSuccess", item, data, response);
}, function(err) {
    Form.fireRule("onFailure", item, data, err);
});

rules.load:
// Show the resume dialog if required
if(Util.getUrlQueryParam("saveChallenge") === "true") {
  Form.showDialog("resumeDialog");
}

// Load dynamic branding
if(data.brand !== false) {
  ux.applyBrand(data.brand);
}

// Load translation if one has been defined
if(data.locale && data.locale !== null) {
  Translation.fetch(data.locale);
}
